import Hero from "./hero";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";

const MovieCard = ({movie}) => {
  const posterUrl =  `https://image.tmdb.org/t/p/w500/${movie.poster_path}`
  const detailUrl = `/movies/${movie.id}`
  return (
    <>
      <div className="col-lg-3 col-md-1 col-sm-2 my-4">
        <div className="card">
          <img src={posterUrl} className ="card-img-top" alt={movie.original_title} />
          <div className="card-body">
            <h5 className="card-title">{movie.original_title}</h5>
            <Link to={detailUrl} className="btn btn-primary">Show details</Link>
          </div>
        </div>
      </div>
    </>
  )
}
const Actionresult = ({searchResults}) => {
  const resultsHtml = searchResults.map((obj, i) => {
    return <MovieCard movie={obj} key={i}/>;
  });
  return (
    <>
      <Hero text="Trending in Action" />
      <div className="container">
        <div className="row">
          {resultsHtml}
        </div>
      </div>
    </>
  );
};

const Action = () => {
    const genre = "action"
    const [result, setResult] = useState([])
    useEffect(()=>{
        fetch(`https://api.themoviedb.org/3/discover/movie?api_key=87872052d5a1309cb9f01b635a167d6b&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&with_genres=${genre}&with_watch_monetization_types=flatrate`)
            .then((response) => response.json())
            .then((data)=>{
                setResult(data.results)
            })
    })
    return <Actionresult searchResults={result}/>
}
export default Action;
